import { Platform } from "react-native";

import colors from "./colors";

export default {
	colors,
	text: {
		fontSize: 18,
		fontFamily: Platform.OS === "android" ? "Roboto" : "Avenir",
		color: colors.dark,
	},
	fonts: {
		alfa_slab: require("./assets/fonts/AlfaSlabOne-Regular.ttf"),
		bowlby_one: require("../assets/fonts/BowlbyOneSC-Regular.ttf"),
	},
};
