import React from "react";
import { View, StyleSheet, TouchableHighlight, Image } from "react-native";

// components
import Text from "./Text";

// styles
import defaultStyles from "../config/styles";

function HelperMenu({ card, onPress, fontFam }) {
	return (
		<TouchableHighlight underlayColor="gray" onPress={onPress}>
			<View style={styles.container}>
				<View style={styles.textContainer}>
					<Text fontFam={fontFam} style={styles.text}>
						{card.name}
					</Text>
				</View>

				<View onPress={onPress} style={styles.card}>
					<Image
						style={styles.cardBack}
						source={require("../assets/cards/card_front.png")}
					/>
					<Image style={styles.cardImage} source={card.image} />
				</View>
			</View>
		</TouchableHighlight>
	);
}

const styles = StyleSheet.create({
	container: {
		alignItems: "center",
		flexDirection: "row",
		height: 150,
		justifyContent: "space-between",
		// marginBottom: 10,
		width: "100%",
		borderColor: "green",
		borderWidth: 1,
	},
	text: {
		color: defaultStyles.colors.white,
		fontSize: 20,
		textShadowColor: "rgba(0, 0, 0, 0.0)",
		textShadowOffset: {
			height: 0,
			width: 0,
		},
		textShadowRadius: 0,
	},
	textContainer: { width: "30%" },
	card: {
		transform: [{ rotate: "20deg" }],
		height: "100%",
		// resizeMode: "contain",
		shadowColor: "#000",
		shadowOffset: {
			width: 2,
			height: 2,
		},
		shadowOpacity: 0.2,
		shadowRadius: 3,
		width: "100%",
	},
	cardBack: { position: "relative", top: 0, right: 0 },
	cardImage: {
		height: "100%",
		position: "relative",
		top: 0,
		right: 0,
		resizeMode: "contain",
		width: "100%",
	},
});

export default HelperMenu;
