import React from "react";
import { View, StyleSheet, TouchableHighlight, Image } from "react-native";

// components
import Text from "./Text";
import GameCard from "./GameCard";

// styles
import defaultStyles from "../config/styles";

function HelperMenu({ card, onPress, fontFam }) {
	return (
		<TouchableHighlight underlayColor="gray" onPress={onPress}>
			<View style={styles.container}>
				<Text fontFam={fontFam} style={styles.text}>
					{card.name}
				</Text>
				<GameCard card={card} />
				<View onPress={onPress} style={styles.card}>
					<Image
						style={styles.cardBack}
						source={require("../assets/cards/card_front.png")}
					/>
					<Image style={styles.cardImage} source={card.image} />
				</View>
			</View>
		</TouchableHighlight>
	);
}

const styles = StyleSheet.create({
	container: {
		flexDirection: "row",
		alignItems: "center",
		height: 70,
		justifyContent: "space-between",
		marginBottom: 10,
		width: "100%",
	},
	highlight: {
		color: "red",
	},
	text: {
		color: defaultStyles.colors.white,
		marginLeft: 10,
		fontSize: 20,
		textShadowColor: "rgba(0, 0, 0, 0.0)",
		textShadowOffset: {
			height: 0,
			width: 0,
		},
		textShadowRadius: 0,
	},
	// image: {
	// 	width: 60,
	// 	height: 70,
	// },
	card: {
		// height: "25%",
		// paddingHorizontal: 1,
		shadowColor: "#000",
		shadowOffset: {
			width: 2,
			height: 2,
		},
		shadowOpacity: 0.2,
		shadowRadius: 3,
		width: "33%",
	},
	cardBack: {},
	cardImage: {
		height: "100%",
		position: "absolute",
		resizeMode: "contain",
		width: "100%",
	},
});

export default HelperMenu;
