import React from "react";
import { View, StyleSheet, TouchableOpacity } from "react-native";

import Text from "./Text";

import defaultStyles from "../config/styles";

function LevelCard({ number, onPress, isSelected }) {
	return (
		<TouchableOpacity onPress={onPress}>
			<View style={[styles.container, styles.notSelectedContainer]}>
				<Text style={styles.text}>{number}</Text>
			</View>
			{isSelected && (
				<View style={[styles.container, styles.selectedContainer]}>
					<Text style={styles.text}>{number}</Text>
				</View>
			)}
		</TouchableOpacity>
	);
}

const styles = StyleSheet.create({
	container: {
		borderRadius: 15,
		height: 150,
		marginRight: 10,
		marginBottom: 10,
		width: 105,
	},
	notSelectedContainer: { backgroundColor: defaultStyles.colors.white },
	selectedContainer: { backgroundColor: defaultStyles.colors.gold },
	text: {
		color: defaultStyles.colors.light_blue,
		fontSize: 60,
		textAlign: "center",
		width: "100%",
	},
});

export default LevelCard;
