import colors from "./colors";
import { Platform } from "react-native";

export default {
	colors,
	text: {
		fontSize: 20,
	},
	shadows: {
		box: {},
		text: {
			innerShadow: {
				textShadowColor: "rgba(0, 0, 0, 0.41)",
				textShadowOffset: { width: 2, height: 2 },
				textShadowRadius: 3,
			},
			// : "2px 2px 3px rgba(0,0,0,0.41)",
			outerShadow: "2px 2px 3px rgba(0,0,0,0.41)",
		},
	},
};
