import React, { useRef } from "react";
import {
	Animated,
	Pressable,
	Image,
	StyleSheet,
	TouchableOpacity,
} from "react-native";

// redux
import { connect } from "react-redux";

import cardFront from "../assets/cards/card_front.png";
import cardBack from "../assets/cards/card_back.png";

function GameCard({ card, handleCardTap, languageToLearn, inStreak = false }) {
	const flipAnimation = useRef(new Animated.Value(0)).current;

	let flipRotation = 0;
	flipAnimation.addListener(({ value }) => (flipRotation = value));

	const flipToFrontStyle = {
		transform: [
			{
				rotateY: flipAnimation.interpolate({
					inputRange: [0, 180],
					outputRange: ["0deg", "180deg"],
				}),
			},
		],
	};

	const flipToBackStyle = {
		transform: [
			{
				rotateY: flipAnimation.interpolate({
					inputRange: [0, 180],
					outputRange: ["180deg", "360deg"],
				}),
			},
		],
	};

	const flipToFront = () => {
		Animated.timing(flipAnimation, {
			toValue: 180,
			duration: 300,
			useNativeDriver: true,
		}).start();
	};

	const flipToBack = () => {
		Animated.timing(flipAnimation, {
			toValue: 0,
			duration: 300,
			useNativeDriver: true,
		}).start();
	};

	// return (
	// 		<Pressable
	// 			style={styles.container}
	// 			onPress={() => {
	// 				!!flipRotation ? flipToBack() : flipToFront();
	// 				handleCardTap(card.languages[languageToLearn]);
	// 			}}
	// 		>
	// 			<Animated.Image
	// 				style={{ ...styles.card, ...styles.cardBack, ...flipToBackStyle }}
	// 				source={cardBack}
	// 			/>
	// 			<Animated.Image
	// 				style={{ ...styles.card, ...styles.cardFront, ...flipToFrontStyle }}
	// 				source={cardFront}
	// 			/>
	// 			<Animated.Image
	// 				style={{ ...styles.card, ...styles.cardFront, ...flipToFrontStyle }}
	// 				source={card.image}
	// 			/>
	// 		</Pressable>
	// 	);
	// }

	return (
		<Pressable
			onPress={() => handleCardTap(card.languages[languageToLearn])}
			style={styles.container}
		>
			<Image
				style={styles.card}
				source={require("../assets/cards/card_front.png")}
			/>
			{inStreak ? (
				<Image
					style={styles.card}
					source={require("../assets/cards/card_back.png")}
				/>
			) : (
				<Image style={styles.card} source={card.image} />
			)}
		</Pressable>
	);
}

const styles = StyleSheet.create({
	container: {
		height: "25%",
		paddingHorizontal: 1,
		shadowColor: "#000",
		shadowOffset: {
			width: 2,
			height: 2,
		},
		shadowOpacity: 0.2,
		shadowRadius: 3,
		width: "33%",
	},
	card: {
		// height: "100%",
		// position: "absolute",
		// resizeMode: "contain",
		// width: "100%",
	},
	cardFront: {
		position: "absolute",
	},
	cardBack: {
		backfaceVisibility: "hidden",
	},
});

const mapStateToProps = state => ({
	languageToLearn: state.matchAndMemory.gameSettings.languageToLearn,
});

export default connect(mapStateToProps)(GameCard);
