import { SET_PROFILE } from "./profileAction";
import { SET_CURRENT_GAME } from "./profileAction";

const initialState = {
	user_profile: {
		gender: "",
		language: "english",
	},
	game_settings: {
		level: "1",
		deck: "numbers_1",
	},
	profile_created: false,
	game_progress: {
		tutorial_completed: true,
		levels: {},
	},
};

const reducer = (state = initialState, action) => {
	switch (action.type) {
		case SET_PROFILE:
			return {
				...state,
				user_profile: {
					...state.user_profile,
					gender: action.payload.gender,
					language: action.payload.language,
				},
				profile_created: true,
			};
		case SET_CURRENT_GAME:
			return {
				...state,
				game_settings: {
					...state.game_settings,
					level: action.payload.level,
					deck: action.payload.deck,
				},
			};
		default:
			return state;
	}
};

export default reducer;
